"use strict";

(function () {
  // init banner
  var $imgContainers = document.querySelectorAll('.j-item-container');

  for (var i = 0, len = $imgContainers.length; i < len; i++) {
    $imgContainers[i].style.width = width() - 32 + 'px';
    $imgContainers[i].style.height = (width() - 32) * bannerRatio + 'px';
  } // init header back


  $('.j-header').on('click', function () {
    window.history.go(-1);
  }); // init play button

  function pause() {
    var $videos = $('.j-video');
    $videos.each(function () {
      $(this)[0].pause();
    });
  } // events


  $('.j-play').on('click', function () {
    pause();
    $(this).css('opacity', 0);
    var $video = $(this).parent().find('video');
    $video.attr('controls', 'controls');
    $video[0].play();
  });
  $('.j-back-top').on('click', function () {
    $(window).scrollTop(0);
  });
})();